import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { API_BASE } from '../config/config'; // Assicurati che il path sia corretto

function Committente({ utente }) {
  const [richieste, setRichieste] = useState([]);
  const [errore, setErrore] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setErrore('Token mancante');
      return;
    }

    fetch(`${API_BASE}/api/richieste/mie`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        console.log('RISPOSTA DAL SERVER → /mie-richieste:', data);

        if (Array.isArray(data)) {
          setRichieste(data);
        } else {
          setErrore(data.error || 'Errore inatteso dal server');
        }
      })
      .catch(err => {
        console.error('Errore connessione:', err);
        setErrore('Errore di connessione al server');
      });
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Ciao {utente?.nome}!</h2>
      <p>Benvenuto nella tua area personale. Qui puoi:</p>
      <ul>
        <li><Link to="/richiesta">Inserire una nuova richiesta di lavoro in quota</Link></li>
      </ul>

      <h3>Le tue richieste</h3>

      {errore && <p style={{ color: 'red' }}>⚠️ {errore}</p>}

      {richieste.length === 0 && !errore ? (
        <p>Non hai ancora inserito richieste.</p>
      ) : (
        <ul>
          {richieste.map(r => (
            <li key={r.id} style={{ marginBottom: '1rem' }}>
              <strong>Categoria:</strong> {r.categoria} <br />
              <strong>Comune:</strong> {r.localita} <br />
              <strong>Data:</strong> {new Date(r.data_inserimento).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Committente;
