import React, { useEffect, useState } from 'react';

function CategoriaForm() {
  const [categorie, setCategorie] = useState([]);
  const [categoriaSelezionata, setCategoriaSelezionata] = useState('');
  const [altroTesto, setAltroTesto] = useState('');

  useEffect(() => {
    fetch('/categorie.json')
      .then(res => res.json())
      .then(data => setCategorie(data));
  }, []);

  const handleChange = (e) => {
    setCategoriaSelezionata(e.target.value);
  };

  const handleAltroChange = (e) => {
    setAltroTesto(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const categoriaFinale = categoriaSelezionata === 'Altro' ? altroTesto : categoriaSelezionata;
    alert(`Hai scelto: ${categoriaFinale}`);
  };

  return (
    <form onSubmit={handleSubmit} style={{ margin: '2rem' }}>
      <h2>Scegli una categoria di lavoro</h2>
      <select value={categoriaSelezionata} onChange={handleChange} required>
        <option value="">-- Seleziona una categoria --</option>
        {categorie.map((gruppo, idx) => (
          <optgroup key={idx} label={gruppo.gruppo}>
            {gruppo.attivita.map((att, i) => (
              <option key={i} value={att}>{att}</option>
            ))}
          </optgroup>
        ))}
        <option value="Altro">Altro (specificaâ€¦)</option>
      </select>

      {categoriaSelezionata === 'Altro' && (
        <>
          <br /><br />
          <input
            type="text"
            placeholder="Specifica il lavoro"
            value={altroTesto}
            onChange={handleAltroChange}
            required
          />
        </>
      )}

      <br /><br />
      <button type="submit">Invia</button>
    </form>
  );
}

export default CategoriaForm;
